var gulp = require('gulp');var rename = require('gulp-rename');var gutil = require('gulp-util');var concat = require('gulp-concat');var selectedSources = [  'src/Three.js',    'src/math/Vector3.js',  'src/math/Box3.js',  'src/math/Quaternion.js',  'src/math/Euler.js',  'src/math/Matrix4.js',  'src/math/Sphere.js',  'src/math/Math.js',  'src/math/Ray.js',  'src/math/Matrix3.js',  'src/math/Plane.js',  'src/math/Vector2.js',    'src/core/EventDispatcher.js',  'src/core/Object3D.js',    'src/cameras/Camera.js',  'src/cameras/PerspectiveCamera.js',  '../CSS3DRenderer.js',  'src/Scene.js'];gulp.task('build-custom', function() {  return gulp.src(selectedSources)    .pipe(concat('threejs_custom.js'))    .pipe(gulp.dest('./build'));});